mapper.update:
        UPDATE oss_app SET
			pvc_yn = #{pvcYn}
        WHERE app_uid = #{appUid}

        UPDATE oss_nas SET
			connect_status	= #{connectStatus}
        WHERE pvc_name = #{pvcName}

        UPDATE oss_nas_app SET
			mount_path	= #{mountPath}
        WHERE nas_id = #{nasId}

        DELETE FROM oss_nas_app
		WHERE
			nas_id = #{nasId}
		AND
			app_uid = #{appUid}
